# String Operations
s = 'DataScience'
print("Original String:", s)
print("Uppercase:", s.upper())
print("Lowercase:", s.lower())
print("Sliced (0:4):", s[0:4])
print("Replace 'Science' with 'Analytics':", s.replace('Science',
'Analytics'))
print("Check if string starts with 'Data':", s.startswith('Data'))
print("Check if string ends with 'Science':", s.endswith('Science'))
print("Split string by 'a':", s.split('a'))
