def test_gen_fixtures_emit_artifact_short_circuit(
    tmp_path: Path, monkeypatch: pytest.MonkeyPatch
) -> None:
    module_path = _write_module(tmp_path)
    output = tmp_path / "fixtures.py"

    delegated = FixturesGenerationResult(
        path=None,
        base_output=output,
        models=(),
        config=ConfigSnapshot(seed=None, include=(), exclude=(), time_anchor=None),
        metadata={},
        warnings=(),
        constraint_summary=None,
        skipped=False,
        delegated=True,
        style="functions",
        scope="function",
        return_type="model",
        cases=1,
    )

    monkeypatch.setattr(
        "pydantic_fixturegen.cli.gen.fixtures.generate_fixtures_artifacts",
        lambda **_: delegated,
    )

    output.write_text("sentinel", encoding="utf-8")

    result = runner.invoke(
        cli_app,
        [
            "gen",
            "fixtures",
            str(module_path),
            "--out",
            str(output),
            "--include",
            "models.User",
        ],
    )

    assert result.exit_code == 0
    assert output.exists()
    assert output.read_text(encoding="utf-8") == "sentinel"


def test_gen_fixtures_emit_error(tmp_path: Path, monkeypatch: pytest.MonkeyPatch) -> None:
    module_path = _write_module(tmp_path)
    output = tmp_path / "fixtures.py"

    error = EmitError(
        "fail",
        details={
            "config": {
                "seed": None,
                "include": ["models.User"],
                "exclude": [],
                "time_anchor": None,
            },
            "warnings": [],
            "base_output": str(output),
            "constraint_summary": {
                "models": [
                    {
                        "model": "models.User",
                        "attempts": 1,
                        "successes": 0,
                        "fields": [],
                    }
                ]
            },
        },
    )

    def raise_error(**_: Any) -> FixturesGenerationResult:
        raise error

    monkeypatch.setattr(
        "pydantic_fixturegen.cli.gen.fixtures.generate_fixtures_artifacts",
        raise_error,
    )

    result = runner.invoke(
        cli_app,
        [
            "gen",
            "fixtures",
            str(module_path),
            "--out",
            str(output),
            "--include",
            "models.User",
        ],
    )

    assert result.exit_code == 30
    assert "fail" in result.stderr


